<?php
class BootstrapMedia extends Component
{
	public $title, $text;
	
	function __construct(Node $parent, $img, $url, $title, $text)
	{
		parent::__construct($parent, 'div', 'media');
		
		$blockMedia = $this->addNode('div');
		$blockMedia->setClass('media-left');
		
		$media = $blockMedia->a($url, '')->img($title, $img);
		$media->setClass('media-object');
		
		$this->text = $this->addNode('div');
		$this->text->setClass('media-body');
		
		$this->title = $this->text->addNode('h4', $title);
		
		$this->text->addText($text);
	}
	
	public static function createDemo(Node $parent, $qty = 1)
	{
		for ($i=0; $i < $qty; $i++)
		{
			$media = new self($parent, 'data:image/svg+xml;base64,', '#', 'Media heading', 'Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at, tempus viverra turpis.');
		}
	}
}
?>